<?phpfunction autoload($className){	$directories = array(		realpath(dirname(__FILE__)) . '/../',		realpath(dirname(__FILE__)) . '/../../app/'	);		$fileName = str_replace('_', '/', $className);	$fileName = strtolower($fileName);	$fileName .= '.class.php';		foreach($directories as $dir)	{		$path = $dir . $fileName;				if(file_exists($path))		{			require_once($path);			return;		}	}		throw new Exception ('Class ' . $className . ' not found!');}spl_autoload_register('autoload');?>