<?phpclass HackJob_Conf_Provider{	protected static $config = array();		public static function setConfig($configPaths)	{		if(!is_array($configPaths))		{			$configPaths = array($configPaths);		}				$mergedConfig = array();				foreach($configPaths as $confPath)		{			$conf = include($confPath);			$mergedConfig = array_merge($mergedConfig, $conf);		}				self::$config = $mergedConfig;		}		public static function get($name, $default = null)	{		$name = strtoupper($name);		if(array_key_exists($name, self::$config))		{			return self::$config[$name];		}				if($default !== null)		{			return $default;		}				throw new HackJob_Conf_Exception('Config key ' . $name . ' not found!');	}}?>