<?phpclass HackJob_Core_Bootstrapper{	public function __construct()	{		error_reporting(E_ALL | E_NOTICE);		set_error_handler(array('HackJob_Error_Handler','handlePHPError'));				$requestUri = $_SERVER['REQUEST_URI'];		$routerClass = HackJob_Conf_Provider::get('ROUTER_CLASS', 'HackJob_Routing_Basic');				$router = new $routerClass();				try		{			ob_start();			$response = $router->delegate($requestUri);			ob_end_clean();						foreach($response->headers as $header)			{				header($header);			}						echo $response->content;		}		catch(Exception $e)		{			$handler = new HackJob_Errror_Handler();			$handler->handleException($e);		}	}}?>