<?phpclass HackJob_Core_Bootstrapper{	public function __construct()	{		$requestUri = $_SERVER['REQUEST_URI'];		$routerClass = HackJob_Conf_Provider::get('ROUTER_CLASS', 'HackJob_Routing_Basic');				$router = new $routerClass();				ob_start();		$response = $router->delegate($requestUri);		ob_end_clean();				foreach($response->headers as $header)		{			header($header);		}				echo $response->content;	}}?>