<?phpclass HackJob_Core_Bootstrapper{	public function __construct()	{		error_reporting(E_ALL | E_NOTICE);		set_error_handler(array('HackJob_Error_Handler','handlePHPError'));				$requestUri = $_SERVER['REQUEST_URI'];		$routerClass = HackJob_Conf_Provider::get('ROUTER_CLASS', 'HackJob_Routing_Basic');				$router = new $routerClass();				try		{			ob_start();			$response = $router->delegate($requestUri);			ob_end_clean();						$response->output();		}		catch(Exception $e)		{			try			{				$handlerClass = HackJob_Conf_Provider::get('EXCEPTION_HANDLER');			}			catch(Exception $confException)			{				$handlerClass = 'HackJob_Error_Handler';			}			$handler = new $handlerClass();			$handler->handleException($e);		}	}}?>