<?phpclass HackJob_Db_Filter{	public $fieldName;	public $filterValue;	public $operator;		public function __construct($fieldName, $filterValue, $operator = '=')	{		$this->fieldName = $fieldName;		$this->filterValue = $filterValue;		$this->operator = $operator;	}		public function getWhereCondition()	{		return implode(' ', array($this->fieldName, $this->operator, ':' . $this->fieldName));	}		public function __toString()	{		return $this->getWhereCondition();	}}?>